import React, { useCallback , useState } from "react"
import PropTypes from 'prop-types'
import PlusOutlined from '@ant-design/icons'
const PostImages = ({ images }) => {
    const [showImagesZoom, setShowImagesZoom] = useState(false)
    
    const onZoom = useCallback(() => {
        setShowImagesZoom(true)
    }, [])
    if (images.length === 1){
        return(
            <>
                <img src={images[0].src} alt={images[0].src} onClick={onZoom} role="presentation"/>
            </>
        )
    }
    if (images.length === 2) {
        return (
            <>
            <img width="50%" src={images[0].src} alt={images[0].src} onClick={onZoom} role="presentation"/>
            <img width="50%" src={images[1].src} alt={images[1].src} onClick={onZoom} role="presentation"/>
            </>
            )
    }
    if (images.length > 2) {
        return (
            <>
            <div>
            <img style={{width : "50%"}} src={images[0].src} alt={images[0].src} onClick={onZoom} role="presentation"/>
            <div
            role="presentation"
            style={{ display : 'inline-block' , width : "50%", textAlign : "center", verticalAlign : "middle" }}
            onClick={onZoom}
            >
                {showImagesZoom ? (
                    <>
                    <img width="50%" src={images[1].src} alt={images[1].src} onClick={onZoom} role="presentation"/>
                    <img width="50%" src={images[2].src} alt={images[2].src} onClick={onZoom} role="presentation"/>
                    </>
                ) : (
                <>
                    <PlusOutlined />
                    <br />
                    { images.length - 1}
                    개의 사진 더보기
                </>
                )}
                                </div>
            </div>
            </>
            )
    }
}

PostImages.PropTypes = {
    images : PropTypes.arrayOf(PropTypes.object)
}


export default PostImages
