const changeNickname =  () => {
    return {
        type : 'CHANGE_NICKNAME',
        data,
    }
}

store.dispatch(changeNickname('boogicho'))

// 이전 상태 , 액션 ==> 다음상태
const rootRedeucer = (state = initicialState, action) => {
    switch (action.type){
        case 'LOG_IN' :
            return {
                ...state,
                user : {
                    ...state.user,
                    isLoggedIn : true,
                    user : action.data,
                }
            }
        case 'LOG_OUT' :
            return {
                ...state,
                user : {
                    ...state.user,
                    isLoggedIn : false,
                    user : null,
                }
            }
    }

}

export default rootRedeucer
