const initialState = {
    user : {
        isLoggedIn : false,
        user : null,
        signUpData : {},
        loginData : {},
    },
    post : {
        mainPosts : [],
    }
}

export const loginAction = (data) => {
    return {
        type : 'LOG_IN',
        data,
    }
}

export const logoutAction = () => {
    return {
        type : 'LOG_OUT',
    }
}

// 비동기
//액션 크리에이터
const changeNickname =  () => {
    return {
        type : 'CHANGE_NICKNAME',
        data,
    }
}

store.dispatch(changeNickname('boogicho'))

// 이전 상태 , 액션 ==> 다음상태
const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'LOG_IN' :
            return {
                ...state,
                user : {
                    ...state.user,
                    isLoggedIn : true,
                    user : action.data,
                }
            }
        case 'LOG_OUT' :
            return {
                ...state,
                user : {
                    ...state.user,
                    isLoggedIn : false,
                    user : null,
                }
            }
    }

}

export default rootReducer
