import React, { useState , useCallback} from 'react'
import Head from 'next/head' 
import { Form , Input , Checkbox, Button } from 'antd'
import styled from 'styled-components'

import AppLayout from '../components/AppLayout'
import useInput from '../hooks/useInput'
import Password from 'antd/lib/input/Password'

const ErrorMessage = styled.div`
    color : red;
`

const Signup = () => {

    const [id , onChangeId] = useInput('')
    const [nickname , onChangeNickname] = useInput('')
    const [password , onChangePassword] = useInput('')
    const [passwordCheck , setPasswordCheck] = useState('')
    const [passwordError , setPasswordError] = useState(false)
    const [term , setTerm] = useState('')
    const [termError , setTermError] = useState(false)
    
    const onChangePasswordCheck = useCallback((e) => {
            setPasswordCheck(e.target.value)
            setPasswordError(e.target.value !== password)
        },[password])

    const onChangeTerm = useCallback((e) => {
            setTerm(e.target.checked) 
            setTermError(false)
        }, [])

    const onSubmit = useCallback(() => {
        if(Password !== passwordCheck){
            return setPasswordError(true)
        }
        if(!term){
            return setTermError(true)
        }
        console.log(id, password, term)
        },[password,passwordCheck,term ])

    return(
    <AppLayout>
    <Head>
        <title>회원가입 | 노드버드</title>
    </Head>
    <Form onFinish={onSubmit}>
        <div>
            <label htmlFor="user-id">아이디</label>
            <br/>
            <Input name="user-id" value={id} required onChange={onChangeId} />
        </div>
        <div>
            <label htmlFor="user-nickname">닉네임</label>
            <br/>
            <Input name="user-nickname" value={nickname} required onChange={onChangeNickname} />
        </div>
        <div>
            <label htmlFor="user-password">비밀번호</label>
            <br/>
            <Input type="password" name="user-password" value={password} required onChange={onChangePassword} />
        </div>
        <div>
            <label htmlFor="user-passwordCheck">비밀번호확인</label>
            <br/>
            <Input type="password" name="user-passwordCheck" value={passwordCheck} required onChange={onChangePasswordCheck} />
        {passwordError && <ErrorMessage>비밀번호가 일치하지 않습니다! </ErrorMessage>}
        </div>
        <div>
            <Checkbox name="user-term" checked={term} onChange={onChangeTerm}>내 말을 잘 들을 것을 동의</Checkbox>
            {termError && <ErrorMessage style={{ color : 'red'}}>약관에 동의해야함</ErrorMessage>}
        </div>
        <div style={{ marginTop : "10px"}}>
            <Button type="primary" htmlType="submit">가입</Button>
        </div>
    </Form>
    </AppLayout>
    )
}

export default Signup
